	force_view boolean;
BEGIN
	IF NEW.schema_force_view IS NULL THEN
		SELECT schema_view FROM distribution.valve_function WHERE id = NEW.id_function INTO force_view;
	ELSE 
		force_view := NEW.schema_force_view;
	END IF;
